import java.util.Scanner;
import java.io.*;

public class Reverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int num=sc.nextInt();
		
		
		
		while(num>0) {
			int rem=num%10;//remainder
			num=num/10;//quotient
			System.out.println(rem);
			
		}
		
			
			
		}
	
	}


